import React from 'react';
import { motion } from 'framer-motion';

const HeroSection = () => {
  const scrollToServices = () => {
    document.getElementById('services').scrollIntoView({ behavior: 'smooth' });
  };

  const scrollToWhatWeDo = () => {
    document.querySelector('.bg-gray-50').scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <section className="relative min-h-[70vh] flex flex-col justify-center items-center text-left px-4 pt-24 pb-16" style={{background: 'url(/eye.jpg) center/cover no-repeat'}}>
      <div className="max-w-3xl w-full mx-auto">
        <motion.h1 
          className="text-4xl md:text-6xl font-extrabold mb-4 leading-tight"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          Dominate Your<br/>
          <span>
            <span className="font-extrabold text-gray-800" style={{letterSpacing: '1px'}}>Local</span>{' '}
            <span className="font-extrabold text-green-400" style={{letterSpacing: '1px'}}>Market</span>
          </span>
        </motion.h1>
        <motion.h2 className="text-2xl md:text-2xl font-semibold text-gray-200 mb-4" initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.4 }}>
          Local Optimization. Simplified.
        </motion.h2>
        <motion.p className="text-lg md:text-xl text-gray-100 mb-8 max-w-2xl" initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.6 }}>
          Transform your Google My Business presence and skyrocket your local search rankings with our proven GMB optimization strategies.
        </motion.p>
        <motion.div className="flex flex-col sm:flex-row gap-4" initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.8 }}>
          <button
            onClick={scrollToServices}
            className="bg-green-400 hover:bg-green-500 text-green-900 font-bold py-3 px-8 rounded-full shadow-md text-lg transition"
          >
            Get Started
          </button>
          <button
            onClick={scrollToWhatWeDo}
            className="border-2 border-white text-white font-bold py-3 px-8 rounded-full text-lg hover:bg-white hover:text-green-900 transition"
          >
            Learn More
          </button>
        </motion.div>
      </div>
    </section>
  );
};

export default HeroSection;
