import React from 'react';

const steps = [
  {
    title: 'Onboarding & Intake',
    desc: 'You provide your business info and access to your Google Business Profile and site (if ads will target it).',
  },
  {
    title: 'GBPâ€¯Rankerâ€¯Pro Optimization',
    desc: 'Our DashClicks team applies weekly professional updates: posts, photo enhancements, Q&A replies, keyword optimization, and AI-backed tuningâ€”all managed via the GBP Ranker Pro system.',
  },
  {
    title: 'Google Ads Pro Campaign Setup',
    desc: 'We create and launch a paid search campaign tailored to your budget tierâ€”setting up targeting, multi-format ads, conversion tracking, and pixel integration.',
  },
  {
    title: 'Ongoing GBP & Ad Management',
    desc: 'GBP: Regular content, images, & profile fine-tuning. Ads: Ongoing budget monitoring, bidding adjustments, and targeting enhancements based on performance.',
  },
  {
    title: 'Monthly Reporting & Insights',
    desc: 'Receive a white-labeled report showcasing: GBP metrics (posts, review growth, rank changes), Ads performance (impressions, clicks, conversions, spend tracking)â€”all in one dashboard.',
  },
  {
    title: 'Strategic Adjustments',
    desc: 'Each month, your campaigns are refined based on: GBP performance (insights, visibility improvements), Ad data (conversion trends, ROI, audience behavior).',
  },
];

const pinColors = [
  'bg-primary-400',
  'bg-blue-400',
  'bg-green-400',
  'bg-yellow-300',
  'bg-purple-400',
  'bg-pink-400',
];

const WorkflowSection = () => (
  <section className="section-padding bg-white">
    <div className="container-custom">
      <div className="text-center mb-12">
        <h2 className="text-4xl md:text-5xl font-bold text-gray-900 mb-4">ðŸš€ Our 6-Step Google Booster Workflow</h2>
        <p className="text-xl text-gray-600 max-w-2xl mx-auto">
          See how we take your local business from setup to ongoing growth and results.
        </p>
      </div>
      <div className="relative w-full flex flex-col items-center">
        {/* Roadmap SVG */}
        <div className="absolute left-0 right-0 top-12 md:top-20 h-40 md:h-56 z-0 pointer-events-none">
          <svg viewBox="0 0 1200 200" fill="none" className="w-full h-full">
            <path d="M0 100 Q 200 0 400 100 T 800 100 T 1200 100" stroke="#222" strokeWidth="14" fill="none" />
            <path d="M0 100 Q 200 0 400 100 T 800 100 T 1200 100" strokeDasharray="16 16" stroke="#fff" strokeWidth="4" fill="none" />
          </svg>
        </div>
        {/* Steps on the roadmap */}
        <div className="relative w-full flex flex-col md:flex-row justify-between items-center z-10 mt-12 md:mt-0">
          {steps.map((step, i) => (
            <div key={step.title} className="flex flex-col items-center w-full md:w-1/6 mb-16 md:mb-0">
              {/* Pin */}
              <div className={`relative flex flex-col items-center mb-4`}>
                <div className={`rounded-full ${pinColors[i % pinColors.length]} w-14 h-14 flex items-center justify-center text-white text-2xl font-bold shadow-lg border-4 border-white z-10`}>{i + 1}</div>
                {/* Pin pointer */}
                <div className={`w-2 h-6 bg-gray-300 rounded-b-full -mt-2`}></div>
              </div>
              {/* Step content */}
              <div className="bg-white rounded-xl shadow-md p-4 md:p-6 text-center border border-primary-100 max-w-xs">
                <h3 className="text-lg md:text-xl font-bold text-gray-900 mb-2">{step.title}</h3>
                <p className="text-gray-600 text-sm md:text-base">{step.desc}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  </section>
);

export default WorkflowSection;
