import React from 'react';
import { Search, Settings, MapPin, Star, BarChart3 } from 'lucide-react';

const steps = [
  {
    icon: <Search className="w-8 h-8 text-primary-500" />,
    title: 'Discovery & Audit',
    desc: 'We start with a deep dive into your current Google My Business presence and local SEO landscape.'
  },
  {
    icon: <Settings className="w-8 h-8 text-primary-500" />,
    title: 'Profile Optimization',
    desc: 'We optimize your GMB profile, business info, categories, and images for maximum visibility.'
  },
  {
    icon: <MapPin className="w-8 h-8 text-primary-500" />,
    title: 'Citation & Content Building',
    desc: 'We build consistent citations and create keyword-rich content to boost your local authority.'
  },
  {
    icon: <Star className="w-8 h-8 text-primary-500" />,
    title: 'Review & Engagement Strategy',
    desc: 'We implement strategies to generate 5-star reviews and engage your customers.'
  },
  {
    icon: <BarChart3 className="w-8 h-8 text-primary-500" />,
    title: 'Ongoing Monitoring & Reporting',
    desc: 'We track your results, provide transparent reports, and continuously optimize your profile.'
  },
];

const WorkflowSection = () => (
  <section className="section-padding bg-gray-50">
    <div className="container-custom">
      <div className="text-center mb-12">
        <h2 className="text-4xl md:text-5xl font-bold text-gray-900 mb-4">Workflow</h2>
        <p className="text-xl text-gray-600 max-w-2xl mx-auto">
          Our proven process ensures your business stands out in local search and gets real results.
        </p>
      </div>
      <div className="flex flex-col md:flex-row md:justify-between gap-8 md:gap-4">
        {steps.map((step, i) => (
          <div key={step.title} className="flex-1 bg-white rounded-2xl shadow-lg p-8 flex flex-col items-center text-center border-t-4 border-primary-300 hover:border-primary-500 transition-all duration-300">
            <div className="mb-4">{step.icon}</div>
            <h3 className="text-xl font-bold mb-2 text-gray-900">{step.title}</h3>
            <p className="text-gray-600 text-base">{step.desc}</p>
            {i < steps.length - 1 && (
              <div className="hidden md:block absolute right-0 top-1/2 transform translate-x-1/2 -translate-y-1/2">
                <div className="w-8 h-1 bg-primary-200 rounded-full"></div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  </section>
);

export default WorkflowSection;
