import React from 'react';

const steps = [
  {
    title: 'Onboarding & Intake',
    desc: 'You provide your business info and access to your Google Business Profile and site (if ads will target it).',
  },
  {
    title: 'GBPâ€¯Rankerâ€¯Pro Optimization',
    desc: 'Our DashClicks team applies weekly professional updates: posts, photo enhancements, Q&A replies, keyword optimization, and AI-backed tuningâ€”all managed via the GBP Ranker Pro system.',
  },
  {
    title: 'Google Ads Pro Campaign Setup',
    desc: 'We create and launch a paid search campaign tailored to your budget tierâ€”setting up targeting, multi-format ads, conversion tracking, and pixel integration.',
  },
  {
    title: 'Ongoing GBP & Ad Management',
    desc: 'GBP: Regular content, images, & profile fine-tuning. Ads: Ongoing budget monitoring, bidding adjustments, and targeting enhancements based on performance.',
  },
  {
    title: 'Monthly Reporting & Insights',
    desc: 'Receive a white-labeled report showcasing: GBP metrics (posts, review growth, rank changes), Ads performance (impressions, clicks, conversions, spend tracking)â€”all in one dashboard.',
  },
  {
    title: 'Strategic Adjustments',
    desc: 'Each month, your campaigns are refined based on: GBP performance (insights, visibility improvements), Ad data (conversion trends, ROI, audience behavior).',
  },
];

const pinColors = [
  'bg-primary-400',
  'bg-blue-400',
  'bg-green-400',
  'bg-yellow-300',
  'bg-purple-400',
  'bg-pink-400',
];

const WorkflowSection = () => (
  <section className="section-padding bg-white">
    <div className="container-custom">
      <div className="text-center mb-12">
        <h2 className="text-4xl md:text-5xl font-bold text-gray-900 mb-4">ðŸš€ Our 6-Step Google Booster Workflow</h2>
        <p className="text-xl text-gray-600 max-w-2xl mx-auto">
          See how we take your local business from setup to ongoing growth and results.
        </p>
      </div>
      <div className="relative w-full flex flex-col items-center min-h-[420px] md:min-h-[500px]">
        {/* Extended Road SVG behind cards */}
        <svg viewBox="0 0 1300 200" className="absolute left-0 right-0 w-full h-[200px] md:h-[260px] z-0" preserveAspectRatio="none">
          <defs>
            <filter id="roadShadow2" x="-20" y="-20" width="1340" height="240">
              <feDropShadow dx="0" dy="8" stdDeviation="8" floodColor="#000" floodOpacity="0.10" />
            </filter>
          </defs>
          {/* Road now continues past the last step */}
          <path d="M0 150 Q 200 50 400 150 T 800 150 T 1200 150 Q 1250 170 1300 120" stroke="#222" strokeWidth="18" fill="none" filter="url(#roadShadow2)" />
          <path d="M0 150 Q 200 50 400 150 T 800 150 T 1200 150 Q 1250 170 1300 120" strokeDasharray="20 20" stroke="#fff" strokeWidth="5" fill="none" />
        </svg>
        {/* Steps: cards above/below, pins point to road, no overlap */}
        <div className="relative w-full flex flex-col md:flex-row justify-between items-center z-10 mt-12 md:mt-0">
          {steps.map((step, i) => {
            // Calculate left % for each step, last pin is a bit left of the very end
            const left = i === steps.length - 1 ? '97%' : `${(i / (steps.length - 1)) * 100}%`;
            // Alternate above/below
            const isAbove = i % 2 === 0;
            return (
              <div
                key={step.title}
                className="absolute flex flex-col items-center w-44 md:w-60"
                style={{ left, top: isAbove ? '10px' : '120px', transform: 'translateX(-50%)' }}
              >
                {/* Pin, small, above the road, pointer to road */}
                <div className="flex flex-col items-center mb-2">
                  <div className={`rounded-full ${pinColors[i % pinColors.length]} w-8 h-8 flex items-center justify-center text-white text-base font-bold shadow-md border-2 border-white z-10`}>{i + 1}</div>
                  <div className="w-1 h-6 bg-gray-300 rounded-b-full -mt-1"></div>
                </div>
                {/* Card above or below the road, no overlap */}
                <div className={`bg-white rounded-xl shadow-md p-4 md:p-6 text-center border border-primary-100 max-w-xs ${isAbove ? 'mb-8' : 'mt-8'}`}
                  style={{ marginTop: isAbove ? '0' : '24px', marginBottom: isAbove ? '24px' : '0' }}
                >
                  <h3 className="text-base md:text-lg font-bold text-gray-900 mb-2">{step.title}</h3>
                  <p className="text-gray-600 text-xs md:text-sm">{step.desc}</p>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  </section>
);

export default WorkflowSection;
