import React from 'react';
import { motion } from 'framer-motion';
import { 
  Search, 
  MapPin, 
  Calendar, 
  Star, 
  MessageSquare, 
  FileText, 
  BarChart3 
} from 'lucide-react';
import ServiceStatsVisual from './ServiceStatsVisual';

const ServicesSection = () => {
  const services = [
    {
      icon: Search,
      title: "GMB Profile Optimization",
      description: "Complete optimization of your Google My Business profile including business information, categories, and attributes for maximum visibility."
    },
    {
      icon: MapPin,
      title: "Citation Building",
      description: "Build consistent business citations across major directories and local platforms to improve your local search authority."
    },
    {
      icon: Calendar,
      title: "Weekly Google Posts",
      description: "Regular, engaging posts to keep your business active and visible in local search results and customer feeds."
    },
    {
      icon: Star,
      title: "Review Generation Strategy",
      description: "Proven strategies to encourage and manage customer reviews, boosting your local reputation and rankings."
    },
    {
      icon: MessageSquare,
      title: "GMB Q&A Optimization",
      description: "Optimize your Q&A section with relevant questions and answers to improve customer engagement and search visibility."
    },
    {
      icon: FileText,
      title: "Keyword-Rich Business Descriptions",
      description: "Compelling, SEO-optimized business descriptions that attract customers and improve search rankings."
    },
    {
      icon: BarChart3,
      title: "Ongoing GMB Monitoring & Reporting",
      description: "Continuous monitoring of your GMB performance with detailed reports and actionable insights for improvement."
    }
  ];

  return (
    <section id="services" className="section-padding bg-white">
      <div className="container-custom">
        <ServiceStatsVisual />
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-gray-900 mb-6">
            Our Services
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Comprehensive Google My Business optimization services designed to boost your local search visibility 
            and drive more customers to your business.
          </p>
        </motion.div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: index * 0.1 }}
              viewport={{ once: true }}
              className="group bg-gray-50 rounded-xl p-8 hover:bg-white hover:shadow-xl transition-all duration-300 border border-gray-100 hover:border-primary-200"
            >
              <div className="w-16 h-16 bg-primary-100 rounded-lg flex items-center justify-center mb-6 group-hover:bg-primary-200 transition-colors duration-300">
                <service.icon className="w-8 h-8 text-primary-600" />
              </div>
              <h3 className="text-xl font-semibold text-gray-900 mb-4 group-hover:text-primary-600 transition-colors duration-300">
                {service.title}
              </h3>
              <p className="text-gray-600 leading-relaxed">
                {service.description}
              </p>
            </motion.div>
          ))}
        </div>

        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.8 }}
          viewport={{ once: true }}
          className="mt-16 text-center"
        >
          <div className="bg-gradient-to-r from-primary-500 to-primary-400 rounded-2xl p-8 text-white">
            <h3 className="text-2xl font-bold mb-4">
              Ready to Dominate Local Search?
            </h3>
            <p className="text-lg mb-6 opacity-90">
              Get started with our comprehensive GMB optimization package and see results within weeks.
            </p>
            <button className="bg-white text-primary-600 font-semibold py-3 px-8 rounded-lg hover:bg-gray-100 transition-colors duration-300">
              Get Started Today
            </button>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default ServicesSection;
